import { useState} from 'react';

export default function Searchbar( {search} ) {

    const [value, setValue] = useState("");

    const submitSearch = (e) => {
        
        e.preventDefault();
        search(value);
    }


    return (
        <>
        <div>
            <nav class="navbar tw-pt-px tw-m-auto tw-w-[500px]">
                <div class="container-fluid mb-3 row" id="searchBar">
                <form class="d-flex" role="search" onSubmit={(e) => submitSearch(e)}>
                    <input class="tw-rounded-md tw-w-full tw-h-[45px] tw-border-none tw-outline-none tw-ring-1 tw-ring-black focus:tw-ring-gray-700 focus:tw-ring-2 tw-duration-500 tw-p-3 tw-mr-2 
                        focus:tw-shadow-lg"
                        type="search" 
                        placeholder="Search" 
                        aria-label="Search"
                        onChange={(e) => setValue(e.target.value)}
                        value={value}>
                        
                    </input>
                    <button class="btn btn-success" type="submit">
                        Search
                    </button>
                </form>
                </div>
            </nav>
        </div>
        </>
    )
}
